void Mundo::RotarOjo()
{
	float dist=sqrt(x_ojo*x_ojo+z_ojo*z_ojo);
	float ang=atan2(z_ojo,x_ojo);
	ang+=0.05f;
	x_ojo=dist*cos(ang);
	z_ojo=dist*sin(ang);
}

void Mundo::Dibuja()
{
	gluLookAt(x_ojo, y_ojo, z_ojo,  // posicion del ojo
		x_vista, y_vista, z_vista,      // hacia que punto mira  (0,0,0) 
			0.0, 0.0, 1.0);      // definimos hacia arriba (eje Y)    

	//aqui es donde hay que poner el codigo de dibujo
	//dibujo del suelo
	glDisable(GL_LIGHTING);
	glEnable(GL_LIGHTING);


	//myCar.Dibuja();
	car1->Dibuja();
	//car2->Dibuja();
	car3->Dibuja();

	myStraightRoad.Dibuja();
	myGrass.Dibuja();   //Descomentar cuando el programa vaya mas avanzado. Lo dejo comentado para tener mas vision del mapa
	//myTemporalStraightRoad.Dibuja();
}

